class BasePopup {
	init(a) {
		return (
			(this.popup = document.querySelector('#popup')),
			this.openPopup(),
			this.awaitExit(),
			this
		);
	}
	openPopup() {
		this.popup.classList.add('active');
	}
	closePopup() {
		this.popup.classList.add('remove');
	}
	awaitExit() {
		let a = this.popup.querySelector('.button [data-exit]');
		a && a.addEventListener('click', () => this.closePopup());
	}
}
class BaseDialog {
	' Dialog Initializer';
	init() {
		(this.dialog = document.querySelector('#dialog')), this.openDialog();
	}
	openDialog() {
		(this.dialog.style.display = 'flex'), (this.isOpen = !0), this.awaitEvent();
	}
	closeDialog() {
		(this.dialog.style.display = 'none'), (this.isOpen = !1);
	}
	awaitEvent() {
		this.dialog.addEventListener('click', ({ target: a, currentTarget: b }) => {
			if (a === b) return this.closeDialog();
		});
	}
}
const Popup = new BasePopup(),
	Dialog = new BaseDialog();
